const chords = require('server/music/chords');
const express = require('express');
const fs = require('fs');
const midi = require('jsmidgen');
const rhythms = require('server/music/rhythms');
const sections = require('server/music/sections');

const PORT = process.env.PORT || 3000;

// Test
const file = new midi.File();
const track = file.addTrack().setTempo(120);
sections.generate(track, 52, [chords.IV, chords.V, chords.vi, chords.V], rhythms.SYNCOPATED);
const bytes = file.toBytes();

const app = express();
app.get('/track.midi', (req, res) => {
  res.contentType('audio/midi').send(new Buffer(bytes, 'binary'));
});

app.listen(PORT, () => {
  console.log(`App listening on port ${PORT}.`);
});
